export function initializeMap(){ymaps.ready((()=>{const e=[45.078245,41.930779];if(document.getElementById("map"))try{const o=new ymaps.Map("map",{center:e,zoom:15,controls:["zoomControl","fullscreenControl"]}),t=new ymaps.Placemark(e,{balloonContent:"Опека"},{preset:"islands#redDotIcon"});o.geoObjects.add(t),o.behaviors.disable("scrollZoom"),o.events.add("click",(function(){o.behaviors.enable("scrollZoom")}))}catch(e){setTimeout(initializeMap,1e3)}else setTimeout(initializeMap,1e3)}))}